import axios from 'axios'
export default {
    name:'Dashboard',
    data: () => ({
        today: new Date(),
        focus: new Date(),
        type: 'month',
        typeToLabel: {
            month: 'Month',
            week: 'Week',
            day: 'Day',
            '4day': '4 Days',
        },
        start: null,
        end: null,
        selectedEvent: {},
        selectedElement: null,
        selectedOpen: false,
        events: [

        ],
    }),
    created(){
        axios.get(`https://us-central1-proyecto-web-km.cloudfunctions.net/getMeetingsBooked?email=${this.$store.state.user.id}`).then(
            response=>{
                let m=response.data
                if(m.asMonitor.length>0){
                    m=response.data.asMonitor
                    m.map(
                            this.events.push(
                                {name:m.subject,
                                    details:`Monitor: ${m.monitor_email} - Lugar: ${m.location}`,
                                    start:new Date(m.start_time._seconds),
                                    end:new Date(m.end_time._seconds),
                                    color:"blue"}
                            )
                    )
                    }
                    if(m.asStudent.length>0){
                        m=response.data.asStudent
                        console.log(m)
                        m.map( o=>{

                                this.events.push(
                                    {name:o.subject,
                                        details:`Monitor: ${o.monitor_email} - Lugar: ${o.location}`,
                                        start:new Date(),
                                        end:new Date(),
                                        color:"green"}
                                )
                        }
                        )
                    }
            }
        )
    },
    computed: {
        title () {
            const { start, end } = this
            if (!start || !end) {
                return ''
            }

            const startMonth = this.monthFormatter(start)
            const endMonth = this.monthFormatter(end)
            const suffixMonth = startMonth === endMonth ? '' : endMonth

            const startYear = start.year
            const endYear = end.year
            const suffixYear = startYear === endYear ? '' : endYear

            const startDay = start.day + this.nth(start.day)
            const endDay = end.day + this.nth(end.day)

            switch (this.type) {
                case 'month':
                    return `${startMonth} ${startYear}`
                case 'week':
                case '4day':
                    return `${startMonth} ${startDay} ${startYear} - ${suffixMonth} ${endDay} ${suffixYear}`
                case 'day':
                    return `${startMonth} ${startDay} ${startYear}`
            }
            return ''
        },
        monthFormatter () {
            return this.$refs.calendar.getFormatter({
                timeZone: 'UTC', month: 'long',
            })
        },
    },
    mounted () {
        this.$refs.calendar.checkChange()
    },
    methods: {
        viewDay ({ date }) {
            this.focus = date
            this.type = 'day'
        },
        getEventColor (event) {
            return event.color
        },
        setToday () {
            this.focus = this.today
        },
        prev () {
            this.$refs.calendar.prev()
        },
        next () {
            this.$refs.calendar.next()
        },
        showEvent ({ nativeEvent, event }) {
            const open = () => {
                this.selectedEvent = event
                this.selectedElement = nativeEvent.target
                setTimeout(() => this.selectedOpen = true, 10)
            }

            if (this.selectedOpen) {
                this.selectedOpen = false
                setTimeout(open, 10)
            } else {
                open()
            }

            nativeEvent.stopPropagation()
        },
        updateRange ({ start, end }) {
            // You could load events from an outside source (like database) now that we have the start and end dates on the calendar
            this.start = start
            this.end = end
        },
        nth (d) {
            return d > 3 && d < 21
                ? 'th'
                : ['th', 'st', 'nd', 'rd', 'th', 'th', 'th', 'th', 'th', 'th'][d % 10]
        },
    },
}
